#![cfg(test)]
use crate::{mock::*, Module, Weight};
use contract::ConfigCache;
use frame_support::{assert_ok, traits::Currency};
use sp_runtime::traits::{Hash, Zero};

#[test]
fn test_build() {
    ExtBuilder::default().build().execute_with(|| {
        let _ = crate::mock::Balances::deposit_creating(&ALICE, 100_000_000);
        let module = include_bytes!(concat!(env!("OUT_DIR"), "/groth16.wasm")).to_vec();
        let hash = <Test as frame_system::Config>::Hashing::hash(&module);
        Contracts::put_code(Origin::signed(ALICE.clone()), module).unwrap();

        let storage_size = ConfigCache::<Test>::subsistence_threshold_uncached()
            .checked_div(<Test as contract::Config>::RentDepositOffset::get())
            .unwrap_or_else(Zero::zero);

        // Endowment should be large but not as large to inhibit rent payments.
        let endowment = <Test as contract::Config>::RentDepositOffset::get()
            .saturating_mul(storage_size + <Test as contract::Config>::StorageSizeOffset::get() as u64)
            .saturating_sub(1u32.into());

        Contracts::instantiate(
            Origin::signed(ALICE.clone()),
            endowment,                           // endowment
            Weight::max_value(),         // gas_limit
            hash.clone(),                // code_hash
            [106, 55, 18, 226].to_vec(), // flip
            b"".to_vec(),                // salt
        )
            .unwrap();
        let addr = Contracts::contract_address(&ALICE, &hash, &b"".to_vec());
        let result = <Module<Test>>::call(Origin::signed(ALICE), addr.clone(), hex::decode("").expect("Decode failed"));
    });
}

#[test]
fn test_bls12_377_verify() {
    let vk_ic:Vec<u8> = hex::decode("71c5a779bb19e0aeb6030768f5001017bbd4edb645306ff1591424c501e7aa8b480fc18d8ed404c893d473afa9b82c01fb3e19bdf85f4fb8acf97d1507b75eb48972c8a10e13f4637f7bee71f89eafc141453e4646c82e188d9ef8cf71fa2f0000").unwrap();
    let vk_ic2:Vec<u8> = hex::decode("4d0869345487bb4070110fa318b0f408dcffce12304589878797615d8a72cc00b6a1e3f50cc0e59770638640090726013db560d3a19288ecedc60ed9ee0afd50ad3ac96db89d99fc249b85a453ef837e9213f8977ac572d818dc43387678a50100").unwrap();
    let vk_encode:Vec<u8> = hex::decode("").unwrap();
    let proof_encode:Vec<u8> = hex::decode("").unwrap();
    let public_input: Vec<u8> =
        hex::decode("a4961fec1c734055a80af91b3fd7405a2cdb741e1867aa7f783ca0959a077110").unwrap();

    assert_ok!(curve::verify(
        0x00,
        vec![vk_ic, vk_ic2],
        vk_encode,
        proof_encode,
        vec![public_input]
    ));
}

